mapper.select:
/* selectSearchBookList 책 목록 가져오기*/ select rownum, a.* from( select b.* ,(select cate_name from category where cate_no=b.cate_no) cate_name, case when b.book_status=0 then null when b.book_status=1 or b.book_status=2 or b.book_status=3 or b.book_status=5 then (select max(rent_end) from book_rent where book_no=b.book_no) end rent_end from book b where b.book_no is not null and b.book_status != 4
and cate_no = #{cateType}
order by b.book_no desc ) A

/* selectSearchBookListCount 책 목록 갯수 가져오기*/ select count(*) from book where book_no is not null and book_status != 4
and cate_no = #{cateType}
order by book_no desc

mapper.update:
update book 
set 
book_status=#{book_status}		
where book_no=#{book_no}

mapper.select:
/* selectBookByBookNo 책 상세보기*/
	select
	b.* ,(select cate_name from category where cate_no=b.cate_no) cate_name
	from book b
	where b.book_no is not null
	and b.book_no=#{book_no}
