mapper.update:
update member 
set 
name=#{name},phone=#{phone}, address=#{address}, email=#{email}, picture =#{picture},
 detail_address=#{detail_address}, birth_date=#{birth_date}

where id=#{id}

update member
set enabled=0
where id=#{id}

update member 
set enabled=1
where id=#{id}

update member
set enabled=2
where id=#{id}
