mapper.select:
/* selectNowRentCount 현재 연체가 아니면서 대여중인 책의 갯수*/
select count(rent_no) 
from book_rent 
where id = #{id} 

and rent_end>sysdate 
and real_end is null

		/* selectOverdueRentCount 현재 연체중인 책의 갯수*/
		select count(rent_no) from book_rent
        where id=#{id}

        and rent_end<sysdate 
        and real_end is null
